#include <string>

// SVG SimSPAD logo encoded in BASE64, as an img tag with an <a> tag pointing to home
std::string page_logo()
{
    return "<a href='/'><img class='logo' src='data:image/svg+xml;base64,' /></a>";
}

// Simple navigation menu as a table
std::string page_menu()
{
    return "<table class='menu' border='0'>"
           "<tbody>"
           "<tr class='menu-row'>"
           "<td class='menu-el'><a href='/'><strong>Home</strong></a></td>"
           "<td class='menu-el'><a href='simspad'><strong>SimSPAD</strong></a></td>"
           "<td class='menu-el'><a href='logs'><strong>Logs</strong></a></td>"
           "<td class='menu-el'><a href='stop'><strong>STOP</strong></a></td>"
           "</tr>"
           "</tbody>"
           "</table>";
}

// CSS Styles for the SimSPAD web page
std::string page_css()
{
    std::string style_str = "<style>"
                            "body{font-family: sans; background: gainsboro;}"
                            ".logo{display: block; margin: auto; width: 50%; max-width: 35em;}"
                            ".logs{font-family: mono; font-size: small;}"
                            ".time{color: steelblue; padding-left: 2em; padding-right: 2em;}"
                            ".info-name{padding-left: 2em; padding-right: 2em;}"
                            ".message{color: black; white-space: pre;}"
                            ".menu-el{width: 25%; height: 2em; text-align: center;}"
                            ".menu-row{height: 2em}"
                            ".menu{width: 400px; height: 2em; border-collapse: collapse; margin-left: auto; margin-right: auto;}"
                            "</style>";

    return style_str;
}

// HTML Header for SimSPAD web page
// Takes a title_name as a string as an argument, which sets the title of the page
std::string page_header(std::string title_name)
{
    std::string pageText = "<!doctype html><html lang='en'><head>"
                           "<title>SimSPAD " +
                           title_name + "</title>";
    pageText += page_css();
    pageText += "</head><body>";
    pageText += page_logo();
    pageText += "<h2>Welcome to SimSPAD</h2>"
                "<p>Access the GitHub Repository for more information and examples in Python and MATLAB on how to interact with SimSPAD server: <a title='SimSPAD github repository' href='https://github.com/WillMatthews/SimSPAD'>https://github.com/WillMatthews/SimSPAD</a></p>"
                "<br/>";
    pageText += page_menu();
    return pageText;
}

// HTML Footer (/body /html)
std::string page_footer()
{
    return "</body></html>";
}

// Homepage defined as a single function
std::string page_welcome()
{
    std::string pageContents = page_header("Home");
    pageContents += page_footer();

    return pageContents;
}
